import React, { Component } from 'react'

export class App extends Component {
  constructor(props) {
    super(props)

    this.state = {
       userInput: "",
       list: []
    }
  }
  changeUserInput(input){
    this.setState({
      userInput: input
    })
  }
  render() {
    return (
      <div className="to-do-list-main">
        <input 
        onChange={(e)=>this.changeUserInput(e.target.value)}
        type="text" 
        value={this.state.userInput}/>
      </div>
    )
  }
}

export default App
